RenderObjectToWidgetElement attachToRenderTree
(BuildOwner owner, [ RenderObjectToWidgetElement element ]) {
    // 如果`element`为null
    if (element == null) {
      // 创建根Element，RenderObjectToWidgetElement
      owner.lockState(() {
        element = createElement();
        assert(element != null);
        element.assignOwner(owner);
      });
      owner.buildScope(element, () {
